define('webpack/lib/ExternalsPlugin', function(require, exports, module) {

  /*
  	MIT License http://www.opensource.org/licenses/mit-license.php
  	Author Tobias Koppers @sokra
  */
  "use strict";
  
  var ExternalModuleFactoryPlugin = require("webpack/lib/ExternalModuleFactoryPlugin");
  
  function ExternalsPlugin(type, externals) {
  	this.type = type;
  	this.externals = externals;
  }
  module.exports = ExternalsPlugin;
  ExternalsPlugin.prototype.apply = function (compiler) {
  	compiler.plugin("compile", (function (params) {
  		params.normalModuleFactory.apply(new ExternalModuleFactoryPlugin(this.type, this.externals));
  	}).bind(this));
  };

});
