define('webpack/lib/JsonpTemplatePlugin', function(require, exports, module) {

  /*
  	MIT License http://www.opensource.org/licenses/mit-license.php
  	Author Tobias Koppers @sokra
  */
  "use strict";
  
  var JsonpMainTemplatePlugin = require("webpack/lib/JsonpMainTemplatePlugin");
  var JsonpChunkTemplatePlugin = require("webpack/lib/JsonpChunkTemplatePlugin");
  var JsonpHotUpdateChunkTemplatePlugin = require("webpack/lib/JsonpHotUpdateChunkTemplatePlugin");
  
  function JsonpTemplatePlugin() {}
  module.exports = JsonpTemplatePlugin;
  JsonpTemplatePlugin.prototype.apply = function (compiler) {
  	compiler.plugin("this-compilation", function (compilation) {
  		compilation.mainTemplate.apply(new JsonpMainTemplatePlugin());
  		compilation.chunkTemplate.apply(new JsonpChunkTemplatePlugin());
  		compilation.hotUpdateChunkTemplate.apply(new JsonpHotUpdateChunkTemplatePlugin());
  	});
  };

});
