define('webpack/lib/dependencies/RequireIncludeDependencyParserPlugin', function(require, exports, module) {

  /*
  	MIT License http://www.opensource.org/licenses/mit-license.php
  	Author Tobias Koppers @sokra
  */
  "use strict";
  
  var AbstractPlugin = require("webpack/lib/AbstractPlugin");
  var RequireIncludeDependency = require("webpack/lib/dependencies/RequireIncludeDependency");
  
  module.exports = AbstractPlugin.create({
  	"call require.include": function callRequireInclude(expr) {
  		if (expr.arguments.length !== 1) return;
  		var param = this.evaluateExpression(expr.arguments[0]);
  		if (!param.isString()) return;
  		var dep = new RequireIncludeDependency(param.string, expr.range);
  		dep.loc = expr.loc;
  		this.state.current.addDependency(dep);
  		return true;
  	}
  });

});
