define('webpack/lib/webworker/WebWorkerTemplatePlugin', function(require, exports, module) {

  /*
  	MIT License http://www.opensource.org/licenses/mit-license.php
  	Author Tobias Koppers @sokra
  */
  "use strict";
  
  var WebWorkerMainTemplatePlugin = require("webpack/lib/webworker/WebWorkerMainTemplatePlugin");
  var WebWorkerChunkTemplatePlugin = require("webpack/lib/webworker/WebWorkerChunkTemplatePlugin");
  
  function WebWorkerTemplatePlugin() {}
  module.exports = WebWorkerTemplatePlugin;
  WebWorkerTemplatePlugin.prototype.apply = function (compiler) {
  	compiler.plugin("this-compilation", function (compilation) {
  		compilation.mainTemplate.apply(new WebWorkerMainTemplatePlugin());
  		compilation.chunkTemplate.apply(new WebWorkerChunkTemplatePlugin());
  	});
  };

});
